import './App.css';
import { useState, useEffect } from 'react';
import Axios from 'axios';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';

//components
import Post from './components/post/Post';
import Profile from './components/profile/Profile';
import Register from './components/register/Register';
import Login from './components/login/Login';
import Navbar from './components/navbar/Navbar';
import CreatePost from './components/create-post/CreatePost';
import Feed from './components/feed/Feed'


function App() {

  Axios.defaults.withCredentials = true;

  const [user, setUser] = useState({
    username: "user",
    followers: [],
    following: [],
    profilePicture: "/default_avatar.jpg"
  });

  const [peekUser, setPeekUser] = useState({
    username: "user",
    followers: [],
    following: [],
    profilePicture: "/default_avatar.jpg"
  });
  const [createPostWindow, setCreatePostWindow] = useState(false);


  const likeDislikeHandler = () => {
    Axios.put(`http://localhost:3001/api/posts/${post._id}/like`, {
        userId: user._id
    }).then((response) => {

        //setLiked(!liked)
        if(liked) {
            setLiked(false)
            setLikesNumber(likesNumber-1)}
        else {
            setLiked(true)
            setLikesNumber(likesNumber+1)}

    })
}

useEffect(() => {
  Axios.get('http://localhost:3001/api/auth/session').then((response) => {
    setUser(response.data.user)
  })
}, [])

  return (
  <>
  {createPostWindow ? <CreatePost user={user} setCreatePostWindow={setCreatePostWindow} /> : <></>}
    <BrowserRouter>
      <Routes>

        <Route path="" element = {user ? <Feed likeDislikeHandler={likeDislikeHandler} setPeekUser={setPeekUser} user={user} setCreatePostWindow={setCreatePostWindow}/> : <Login setUser={setUser} />}/>
        <Route path="register" element={user ? <Feed user={user} setCreatePostWindow={setCreatePostWindow}/> : <Register setUser={setUser}/>} />
        <Route path="login" element={user ? <Feed user={user} setCreatePostWindow={setCreatePostWindow}/> : <Login setUser={setUser}/>} />
        <Route path="profile" element = {user ? <Profile setUser={setUser} setCreatePostWindow={setCreatePostWindow} user={user}/> : <Login setUser={setUser}/>}/>
        <Route path="user" element = {user ? <Profile setUser={setPeekUser} setCreatePostWindow={setCreatePostWindow} user={peekUser}/> : <Login setUser={setUser}/>}/>

      </Routes>

    </BrowserRouter>
  </>
  )
}

export default App;
