import './suggestions.css'
import Axios from 'axios';
import {useEffect, useState} from 'react';

import ExploreProfile from '../explore-profile/ExploreProfile';

export default function Suggestions({user}) {

        const followUser = (s, e)=>{
            console.log("Following" + s.username);

            Axios.put(`http://localhost:3001/api/users/${s._id}/follow`, {
                userId: user._id
            }).then(response => {

                console.log(response)
                e.target.innerHTML = "Followed"

            })         
        }
        const [suggestedUsers, setSuggestedUsers] = useState([])

        useEffect(() => {
            console.log("Current user" + user._id)
            Axios.get('http://localhost:3001/api/users/suggestions', {
            userId:user._id
        }).then((response) => {
            console.log(response)
            setSuggestedUsers(response.data)
        })
        }, []);



    return (
        <div className='suggestions'>
                <h4>Suggestions For You</h4>

                {
                         suggestedUsers.map((s) => (

                <div className="suggestion">
                <ExploreProfile sgUser={s}/>
                <span
                onClick={(e) => followUser(s, e)}

                >Follow</span>
                </div>

                        ))

                    }

        </div>
    )
}
