import './App.css';
import { useState, useEffect } from 'react';
import Axios from 'axios';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';

//components
import Post from './components/post/Post';
import Profile from './components/profile/Profile';
import Register from './components/register/Register';
import Login from './components/login/Login';
import Navbar from './components/navbar/Navbar';


function App() {

  Axios.defaults.withCredentials = true;

  const [user, setUser] = useState();



useEffect(() => {
  Axios.get('http://localhost:3001/api/auth/session').then((response) => {
    console.log(response.data.logged)
    console.log(response.data.user)
    setUser(response.data.user)
  })
}, [])

  return (
  <>
    <BrowserRouter>
      <Routes>

        <Route path="" element = {user ? <Navbar/> : <Login setUser={setUser} />}/>
        <Route path="register" element={user ? <Navbar/> : <Register/>}/>
        <Route path="login"> {user ? Navbar : Login} </Route>
        <Route path="post" element = {<><Navbar/><Post/></>}/>
        <Route path="profile" element = {<><Navbar/><Profile/></>}/>
      </Routes>

    </BrowserRouter>
  </>
  )
}

export default App;
