import logo from './logo.svg';
import './App.css';
import {useState, useEffect} from 'react'
import Axios from 'axios'

import { render } from "react-dom";
import {
  BrowserRouter,
  Routes,
  Route
} from "react-router-dom";

import Feed from './components/feed/Feed'
import Messenger from './components/messenger/Messenger'
import Register from './components/register/Register'
import Login from './components/login/Login'




function App() {

  const [user, setUser] = useState(
{    username: "user",
    followers: [],
    following: [],
    profilePicture: "/default_avatar.jpg"}
  )
  const [peekUser, setPeekUser] = useState(
    {    username: "user",
    followers: [],
    following: [],
    profilePicture: "/default_avatar.jpg"}
  )
  const [createPostWindow ,setCreatePostWindow] = useState(false)

  const peekUserHandler = (usrnm) => {
    Axios.get(`http://localhost:3001/api/users?user=${usrnm}`).then((res) => {
      setPeekUser(res.data);})
  }


  useEffect(() => {
    Axios.get('http://localhost:3001/api/auth/session').then((response) => {
      setUser(response.data)
    })
  }, [])

//user, setCreatePostWindow, setPeekUser, peekUserHandler
//setCreatePostWindow, user


  return (
    <div className="App">
      {/* <Feed user={user} setCreatePostWindow={setCreatePostWindow} setPeekUser={setPeekUser} peekUserHandler={peekUserHandler} /> */}

      {/* <Messenger user ={user} setCreatePostWindow={setCreatePostWindow} /> */}



      <BrowserRouter>
    <Routes>
      <Route path="/" element={ user.createdAt ? <Register setUser={setUser}/> :  <Login setUser={setUser}/>} />
      <Route path="register" element={<Register setUser={setUser}/>} />
      <Route path="login" element={<Login setUser={setUser}/>} />
    </Routes>
  </BrowserRouter>


    </div>
  );
}

export default App;
