import './App.css';
import { useState, useEffect } from 'react';
import Axios from 'axios';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';

//components
import Post from './components/post/Post';
import Profile from './components/profile/Profile';
import Register from './components/register/Register';
import Login from './components/login/Login';
import Navbar from './components/navbar/Navbar';
import CreatePost from './components/create-post/CreatePost';


function App() {

  Axios.defaults.withCredentials = true;

  const [user, setUser] = useState();
  const [createPostWindow, setCreatePostWindow] = useState(false);

useEffect(() => {
  Axios.get('http://localhost:3001/api/auth/session').then((response) => {
    setUser(response.data.user)
  })
}, [])

  return (
  <>
  {createPostWindow ? <CreatePost/> : <></>}
    <BrowserRouter>
      <Routes>

        <Route path="" element = {user ? <Navbar setCreatePostWindow={setCreatePostWindow}/> : <Login setUser={setUser} />}/>
        <Route path="register" element={user ? <Navbar/> : <Register setUser={setUser} />}/>
        <Route path="login"> {user ? Navbar : Login} </Route>
        <Route path="post" element = {<><Navbar setCreatePostWindow={setCreatePostWindow} /><Post/></>}/>
        <Route path="profile" element = {<><Navbar setCreatePostWindow={setCreatePostWindow} /><Profile setUser={setUser} user={user}/></>}/>

      </Routes>

    </BrowserRouter>
  </>
  )
}

export default App;
