import './profile.css'
import { MoreHoriz} from "@material-ui/icons";
import {useEffect, useState} from "react"
import Axios from 'axios';

import Navbar from '../navbar/Navbar'


export default function Profile({user, setUser, setCreatePostWindow}) {

    const [userProfile, setUserProfile] = useState({
        username: "user",
        followers: [],
        following: [],
        profilePicture: "/default_avatar.jpg"
    })
    const [posts, setPosts] = useState([]);
    const [uploadNewAvatar, setUploadNewAvatar] = useState(false)

    Axios.defaults.withCredentials = true;

    const [file, setFile] = useState(null);
    const [caption, setCaption] = useState("")

    const submitHandler = async (e) => {
        e.preventDefault();
        const newPost = {
            userId: user._id,
            desc: caption
        }
        if(file){
            const data = new FormData();
            const fileName = Date.now() + file.name;
            data.append('name', fileName);
            data.append('file', file);

            newPost.img = fileName;

            try{
                await Axios.post('http://localhost:3001/api/upload', data)
                window.location.reload()
            }catch(err){}

        }
    }

    useEffect(() => {
        if(!user){
            Axios.get('http://localhost:3001/api/auth/session').then((response) => {
                setUser(response.data.user);
                setUserProfile(response.data.user);
                Axios.get(`http://localhost:3001/api/posts/profile/${response.data.user.username}`)
                .then((response) => {setPosts(
                    response.data.sort((p1, p2) => {
                        return new Date(p2.createdAt) - new Date(p1.createdAt);
                      })
                );})
              })

        } else {
        Axios.get(`http://localhost:3001/api/users?user=${user.username}`)
        .then((response) => {         
            setUserProfile(response.data);
            Axios.get(`http://localhost:3001/api/posts/profile/${user.username}`)
            .then((response) => {setPosts(
                response.data.sort((p1, p2) => {
                    return new Date(p2.createdAt) - new Date(p1.createdAt);
                  })
            );})
        })}


      }, [])



    return (
        <><Navbar setCreatePostWindow={setCreatePostWindow} user={user} />
         {uploadNewAvatar && (
               <div className="input-form">
               <svg aria-label="Icon to represent media such as images or videos"  color="#262626" fill="#262626" height="77" role="img" viewBox="0 0 97.6 77.3" width="96"><path d="M16.3 24h.3c2.8-.2 4.9-2.6 4.8-5.4-.2-2.8-2.6-4.9-5.4-4.8s-4.9 2.6-4.8 5.4c.1 2.7 2.4 4.8 5.1 4.8zm-2.4-7.2c.5-.6 1.3-1 2.1-1h.2c1.7 0 3.1 1.4 3.1 3.1 0 1.7-1.4 3.1-3.1 3.1-1.7 0-3.1-1.4-3.1-3.1 0-.8.3-1.5.8-2.1z" fill="currentColor"></path><path d="M84.7 18.4L58 16.9l-.2-3c-.3-5.7-5.2-10.1-11-9.8L12.9 6c-5.7.3-10.1 5.3-9.8 11L5 51v.8c.7 5.2 5.1 9.1 10.3 9.1h.6l21.7-1.2v.6c-.3 5.7 4 10.7 9.8 11l34 2h.6c5.5 0 10.1-4.3 10.4-9.8l2-34c.4-5.8-4-10.7-9.7-11.1zM7.2 10.8C8.7 9.1 10.8 8.1 13 8l34-1.9c4.6-.3 8.6 3.3 8.9 7.9l.2 2.8-5.3-.3c-5.7-.3-10.7 4-11 9.8l-.6 9.5-9.5 10.7c-.2.3-.6.4-1 .5-.4 0-.7-.1-1-.4l-7.8-7c-1.4-1.3-3.5-1.1-4.8.3L7 49 5.2 17c-.2-2.3.6-4.5 2-6.2zm8.7 48c-4.3.2-8.1-2.8-8.8-7.1l9.4-10.5c.2-.3.6-.4 1-.5.4 0 .7.1 1 .4l7.8 7c.7.6 1.6.9 2.5.9.9 0 1.7-.5 2.3-1.1l7.8-8.8-1.1 18.6-21.9 1.1zm76.5-29.5l-2 34c-.3 4.6-4.3 8.2-8.9 7.9l-34-2c-4.6-.3-8.2-4.3-7.9-8.9l2-34c.3-4.4 3.9-7.9 8.4-7.9h.5l34 2c4.7.3 8.2 4.3 7.9 8.9z" fill="currentColor"></path><path d="M78.2 41.6L61.3 30.5c-2.1-1.4-4.9-.8-6.2 1.3-.4.7-.7 1.4-.7 2.2l-1.2 20.1c-.1 2.5 1.7 4.6 4.2 4.8h.3c.7 0 1.4-.2 2-.5l18-9c2.2-1.1 3.1-3.8 2-6-.4-.7-.9-1.3-1.5-1.8zm-1.4 6l-18 9c-.4.2-.8.3-1.3.3-.4 0-.9-.2-1.2-.4-.7-.5-1.2-1.3-1.1-2.2l1.2-20.1c.1-.9.6-1.7 1.4-2.1.8-.4 1.7-.3 2.5.1L77 43.3c1.2.8 1.5 2.3.7 3.4-.2.4-.5.7-.9.9z" fill="currentColor"></path></svg>
               <h4>Drag photos and videos here</h4>


               <label className='upload-btn' htmlFor='new-post'>Select from computer</label>
                  <input
                style={{ display:'none' }}
                type="file"
                id="new-post"
                accept=".png,.jpeg,.jpg"
                onChange={(e) => setFile(e.target.files[0])}
              />
                </div>)}


        <div className='profile'>
            <div className="profile-info">
                <div
                onClick={() => {
                    setUploadNewAvatar(true)
                }}

                className="profile-picture">
                    <img src={process.env.REACT_APP_PULIC_FOLDER + userProfile.profilePicture} alt="" className="profile-icon" />
                </div>
                <div className="profile-summary">
                    <div className="profile-summary-top">
                        <h2 className="profile-username">{userProfile.username}</h2>
                        <button className="btn message">Message</button>
                        <button className="btn follow">Follow</button>
                        <MoreHoriz className='more-btn' />
                    </div>
                    <div className="profile-summary-center">
                       <p><span>{posts.length}</span>posts</p>
                       <p><span>{userProfile.followers.length}</span>followers</p>
                       <p><span>{userProfile.following.length}</span>following</p>
                    </div>
                    <div className="profile-summary-bottom">
                        <p className="common-followers">
                            Followed by <span>john_1919</span>, <span>john_1919</span>,<span>john_1919</span> + 10 more
                        </p>
                    </div>

                </div>

                </div>{/**PROFILE INFO */}
                <div className="profile-posts">


                    {

                        posts.map((p) => (
                            <div className="post-item">
                        <img src={process.env.REACT_APP_PULIC_FOLDER +"/"+ p.img} alt="" className="post-img" />
                    </div>
                        ))



                    }



                </div>
        </div>
        </> )
}
