import './messenger.css';
import {useEffect, useState} from "react"
import Axios from 'axios';

import Navbar from '../navbar/Navbar'
import ChatRooms from './ChatRooms/ChatRooms'
import Chat from './Chat/Chat'
import CreateRoom from '../create-room/CreateRoom'

export default function Messenger({setCreatePostWindow, user}) {

    const [activeRoom, setActiveRoom] = useState(null)
    const [showCreateRoomWindow, setShowCreateRoomWindow] = useState(false);

    return (
            <><Navbar setCreatePostWindow={setCreatePostWindow} user={user} />

            {showCreateRoomWindow && <CreateRoom/>}
            <div className="messenger">
                <ChatRooms setShowCreateRoomWindow={setShowCreateRoomWindow} setActiveRoom={setActiveRoom} activeRoom={activeRoom} user={user} />
                <Chat user={user} activeRoom={activeRoom}/>

            </div>

</>
    )
}
