import logo from './logo.svg';
import './App.css';
import {useState, useEffect} from 'react'
import Axios from 'axios'

import { render } from "react-dom";
import {
  BrowserRouter,
  Routes,
  Route
} from "react-router-dom";

import Feed from './components/feed/Feed'
import Messenger from './components/messenger/Messenger'
import Register from './components/register/Register'
import Login from './components/login/Login'
import Profile from './components/profile/Profile'
import CreatePost from './components/create-post/CreatePost'




function App() {

  const [user, setUser] = useState(
{    username: "user",
    followers: [],
    following: [],
    profilePicture: "/default_avatar.jpg"}
  )

  const [logged, setLogged] = useState(false)
  const [peekUser, setPeekUser] = useState(
    {    username: "user",
    followers: [],
    following: [],
    profilePicture: "/default_avatar.jpg"}
  )
  const [createPostWindow ,setCreatePostWindow] = useState(false)

  const peekUserHandler = (usrnm) => {
    Axios.get(`http://localhost:3001/api/users?user=${usrnm}`).then((res) => {
      setPeekUser(res.data);})
  }




  useEffect(() => {
    Axios.get('http://localhost:3001/api/auth/session').then((response) => {
      setUser(response.data.user)
      setLogged(response.data.logged) 
    })
  }, [])

//user, setCreatePostWindow, setPeekUser, peekUserHandler
//setCreatePostWindow, user


  return (
    <div className="App">
      <>
      <CreatePost user={user} setCreatePostWindow={setCreatePostWindow} />
      <BrowserRouter>
    <Routes>
      <Route path="/" element={ logged ? <Feed user={user} setCreatePostWindow={setCreatePostWindow} setPeekUser={setPeekUser} peekUserHandler={peekUserHandler} />  : <Login setUser={setUser}/>} />
      <Route path="register" element={!logged ? <Register setUser={setUser}/> : <Feed user={user} setCreatePostWindow={setCreatePostWindow} setPeekUser={setPeekUser} peekUserHandler={peekUserHandler} />} />
      <Route path="login" element={!logged ? <Login setUser={setUser}/> : <Feed user={user} setCreatePostWindow={setCreatePostWindow} setPeekUser={setPeekUser} peekUserHandler={peekUserHandler} />} />
      <Route path="messenger" element={ logged ? <Messenger setCreatePostWindow={setCreatePostWindow} user={user}/> : <Login setUser={setUser}/>} />
      <Route path="profile" element={ logged ? <Profile user={user} setUser={setUser} setCreatePostWindow={setCreatePostWindow} />  : <Login setUser={setUser}/>} />
    </Routes>
  </BrowserRouter>

  </>
    </div>
  );
}

export default App;
